    for (int x : arr) sum += x;
    return sum;
}

int main() {
    int n;
    cin >> n;
    vector<int> arr(n);
    for (int i = 0; i < n; i++) cin >> arr[i];
    cout << sumArray(arr);
    return 0;
}
